import React from 'react';
import styled from 'styled-components';

const HeroSection = styled.section`
  display: flex;
  align-items: center;
  padding: 60px 40px;
  background: #ffffff;
`;

const Text = styled.div`
  flex: 1;
  padding-right: 20px;
`;

const Title = styled.h2`
  font-size: 36px;
  margin-bottom: 20px;
`;

const Subtitle = styled.p`
  font-size: 18px;
  margin-bottom: 30px;
`;

const Button = styled.button`
  background: #ff7a00;
  color: white;
  border: none;
  padding: 12px 24px;
  font-size: 16px;
  cursor: pointer;
  border-radius: 4px;
`;

const Image = styled.img`
  flex: 1;
  max-width: 100%;
  border-radius: 8px;
`;

export default function Hero() {
  return (
    <HeroSection>
      <Text>
        <Title>Psicologia e Neuropsicologia</Title>
        <Subtitle>
          Dedicamo-nos a promover o bem-estar e saúde mental através de uma abordagem integrada e personalizada para cada paciente.
        </Subtitle>
        <Button>Agende sua consulta</Button>
      </Text>
      <Image src="https://images.unsplash.com/photo-1566753323558-f4e0952af115?auto=format&fit=crop&w=600&q=80" alt="Consultation" />
    </HeroSection>
  );
}
