import React from 'react';
import styled from 'styled-components';

const InfoSection = styled.section`
  background: #d0f4ff;
  padding: 60px 30px;
  text-align: center;
`;

const Title = styled.h2`
  font-size: 32px;
  color: #2d82b7;
  margin-bottom: 24px;
`;

const Content = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 30px;
  justify-content: center;
`;

const Card = styled.div`
  background: white;
  padding: 24px;
  border-radius: 12px;
  max-width: 300px;
  box-shadow: 0 6px 16px rgba(0,0,0,0.05);
  text-align: left;
  border-left: 6px solid ${props => props.color || '#2d82b7'};
`;

const CardTitle = styled.h4`
  font-size: 20px;
  color: #0a2239;
  margin-bottom: 10px;
`;

const Text = styled.p`
  color: #444;
  font-size: 14px;
`;

export default function InfoTEA() {
  return (
    <InfoSection id="info">
      <Title>Entendendo o TEA</Title>
      <Content>
        <Card color="#3da5d9">
          <CardTitle>O que é TEA?</CardTitle>
          <Text>O Transtorno do Espectro Autista é uma condição do neurodesenvolvimento que afeta a comunicação, comportamento e interação social.</Text>
        </Card>
        <Card color="#f25c54">
          <CardTitle>Sinais Precoces</CardTitle>
          <Text>Evita contato visual, atraso na fala, interesse restrito por objetos ou atividades são sinais comuns a serem observados.</Text>
        </Card>
        <Card color="#06d6a0">
          <CardTitle>Importância do Diagnóstico</CardTitle>
          <Text>O diagnóstico precoce permite intervenções terapêuticas que promovem mais autonomia e qualidade de vida.</Text>
        </Card>
        <Card color="#ffd166">
          <CardTitle>Como Ajudar?</CardTitle>
          <Text>Compreensão, empatia e acolhimento são essenciais para a inclusão e desenvolvimento de pessoas com TEA.</Text>
        </Card>
      </Content>
    </InfoSection>
  );
}
