import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import styled from 'styled-components';

const BannerWrapper = styled.div`
  width: 100vw;
  height: 600px;
  position: relative;  /* importante para posicionar a mensagem sobreposta */
  overflow: hidden;
`;

const StyledSwiper = styled(Swiper)`
  width: 100%;
  height: 100%;
`;

const SlideImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

// Mensagem sobreposta (posição absoluta)
const MessageOverlay = styled.div`
  position: absolute;
  top: 30px;   /* distância do topo do banner */
  left: 50%;
  transform: translateX(-50%);
  background-color: rgba(230, 240, 250, 0.7); /* azul claro semitransparente */
  padding: 20px 40px;
  border-radius: 10px;
  text-align: center;
  z-index: 10;
  max-width: 90vw;
`;

const MessageText = styled.h2`
  font-size: 24px;
  color: #2d82b7;
  margin: 0 0 12px 0;
`;

const Button = styled.button`
  padding: 10px 24px;
  font-size: 16px;
  color: #2d82b7;
  background: transparent;
  border: 2px solid #2d82b7;
  border-radius: 25px;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease;

  &:hover {
    background: #2d82b7;
    color: white;
  }
`;

export default function BannerCarousel() {
  return (
    <BannerWrapper>
      <MessageOverlay>
        <MessageText>Encontre especialistas e orientação</MessageText>
        <Button>Saiba Mais</Button>
      </MessageOverlay>

      <StyledSwiper
        modules={[Navigation, Autoplay]}
        navigation
        autoplay={{ delay: 4000 }}
        loop
      >
        <SwiperSlide>
          <SlideImage src="/images/banner1.jpg" alt="Banner 1" />
        </SwiperSlide>
        <SwiperSlide>
          <SlideImage src="/images/banner2.jpg" alt="Banner 2" />
        </SwiperSlide>
        <SwiperSlide>
          <SlideImage src="/images/banner1.jpg" alt="Banner 3" />
        </SwiperSlide>
      </StyledSwiper>
    </BannerWrapper>
  );
}
