import React from 'react';
import styled from 'styled-components';

const HeroSection = styled.section`
  display: flex;
  padding: 40px;
  align-items: center;
  background: #eaf3f8;
  gap: 40px;
  flex-wrap: wrap;
`;

const Text = styled.div`
  flex: 1;
  min-width: 300px;
`;

const Title = styled.h1`
  font-size: 36px;
  color: #0a2239;
  margin-bottom: 20px;
`;

const Subtitle = styled.p`
  font-size: 18px;
  color: #2d2d2d;
  margin-bottom: 30px;
  line-height: 1.5;
`;

const Button = styled.button`
  background-color: #2d82b7;
  color: white;
  border: none;
  padding: 14px 28px;
  font-size: 16px;
  font-weight: 600;
  border-radius: 6px;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #1a5a82;
  }
`;

const Image = styled.img`
  flex: 1;
  max-width: 400px;
  border-radius: 12px;
  box-shadow: 0 6px 14px rgb(0 0 0 / 0.15);
`;

export default function Hero() {
  return (
    <HeroSection>
      <Text>
        <Title>Suporte Especializado para Crianças com TEA</Title>
        <Subtitle>
          Oferecemos acompanhamento psicológico e neuropsicológico individualizado, focado no desenvolvimento e bem-estar do seu filho e de toda a família.
        </Subtitle>
        <Button>Agende uma Avaliação</Button>
      </Text>
      <Image src="/imges/family2.jpg" alt="Criança sendo acompanhada" />
    </HeroSection>
  );
}
