// MainPageWithFeatures.js
// Integração de BackToTop, DarkMode e Scroll Animations

import React, { useState, useEffect } from 'react';
import { ThemeProvider, createGlobalStyle } from 'styled-components';
import Header from '../components/Header';
import BannerCarousel from '../components/BannerCarousel';
import Hero from '../components/Hero';
import About from '../components/About';
import InfoTEA from '../components/InfoTEA';
import Services from '../components/Services';
import Contact from '../components/Contact';
import Footer from '../components/Footer';
import BackToTop from '../components/BackToTop';
import DarkModeToggle from '../components/DarkModeToggle';

const GlobalStyle = createGlobalStyle`
  *, *::before, *::after { box-sizing: border-box; }
  html, body { margin: 0; padding: 0; overflow-x: hidden; }
  body { background: ${({ theme }) => theme.bg}; color: ${({ theme }) => theme.text}; transition: background 0.3s ease, color 0.3s ease; }
`;

const lightTheme = { bg: '#f0f4f8', text: '#0a2239' };
const darkTheme = { bg: '#0a0a0f', text: '#e1e1e6' };

export function useRevealOnScroll() {
  useEffect(() => {
    const items = document.querySelectorAll('[data-reveal]');
    const observer = new IntersectionObserver(
      entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('revealed');
            observer.unobserve(entry.target);
          }
        });
      }, { threshold: 0.1 }
    );
    items.forEach(item => observer.observe(item));
    return () => observer.disconnect();
  }, []);
}

export default function Home() {
  const [darkMode, setDarkMode] = useState(false);

  useRevealOnScroll();

  return (
    <ThemeProvider theme={darkMode ? darkTheme : lightTheme}>
      <>
        <GlobalStyle />
        <Header>
          <DarkModeToggle darkMode={darkMode} setDarkMode={setDarkMode} />
        </Header>
        <BannerCarousel />
        <Hero data-reveal="true" />
        <About data-reveal="true" />
        <InfoTEA data-reveal="true" />
        <Services data-reveal="true" />
        <Contact data-reveal="true" />
        <Footer />
        <BackToTop />
      </>
    </ThemeProvider>
  );
}
