import React from 'react';
import styled from 'styled-components';

const HeroSection = styled.section`
  display: flex;
  align-items: center;
  padding: 80px 40px;
  background: #f7f9fb;
`;

const Text = styled.div`
  flex: 1;
  padding-right: 24px;
`;

const Title = styled.h2`
  font-size: 32px;
  margin-bottom: 16px;
  color: #0a2239;
`;

const Subtitle = styled.p`
  font-size: 16px;
  line-height: 1.6;
  color: #333;
  margin-bottom: 24px;
`;

const Button = styled.a`
  display: inline-block;
  background: #0a2239;
  color: #fff;
  padding: 12px 28px;
  font-size: 14px;
  border-radius: 4px;
  text-decoration: none;
  &:hover {
    background: #081b2f;
  }
`;

const Image = styled.img`
  flex: 1;
  max-width: 100%;
  border-radius: 8px;
`;

export default function Hero() {
  return (
    <HeroSection>
      <Text>
        <Title>Bem‑estar e Cuidado Familiar</Title>
        <Subtitle>
          Dedicamo‑nos a apoiar sua família com serviços de psicologia e neuropsicologia,
          oferecendo um ambiente seguro e acolhedor para cada etapa da vida.
        </Subtitle>
        <Button href="#contact">Agende sua Consulta</Button>
      </Text>
      <Image src="/images/family1.jpg" alt="Família acolhedora" />
    </HeroSection>
  );
}
